package javaCourse2020;

public class SwitchStatememt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String operator="-";
	//double num=10;
		switch (operator) {
		case"-":
			System.out.println("*");
			break;
		case "*":
			System.out.println("-");
			//break;
		case"+":
			System.out.println("+");
			//break;
		//case '/':
			System.out.println("/");
		//	break;
		default:
			System.out.println("Not Operator");
			//break;
		}

	}

}
